import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { ResultCard } from './Component/ResultCard.js';
import './Home.css';

export const Home = () => {

    const [topAnime, setTopAnime] = useState([]);
    const [search, setSearch] = useState("");
    const [results, setResults] = useState([]);
    const baseUrl = "https://api.jikan.moe/v3";

    useEffect(() => {
        // fetch(`${baseUrl}/top/anime`)
        //     .then(res => res.json())
        //     .then((data) => {
        //         if (!data.errors) {

        //             setTopAnime(data.top);
        //         }

        //     });
        axios.get(`${baseUrl}/top/anime`)
            .then(res => {
                setTopAnime(res.data.top);
            })
    })

    // use for searching
    // prevents api calling everytime user enters a character
    let cancelToken;

    const onChange = async (e) => {
        // e.preventDefault();

        // setSearch(e.target.value);

        // const query = e.target.value;

        // const url = "https://api.jikan.moe/v3";

        // fetch(`${url}/search/anime?q=${query}&page=1`)
        //     .then(res => res.json())
        //     .then((data) => {
        //         if (!data.errors) {
        //             setResults(data.results);
        //         }
        //         else {
        //             setResults([]);
        //         }

        //     })
        e.preventDefault();
        setSearch(e.target.value);
        const query = e.target.value;

        if (typeof cancelToken != typeof undefined) {
            cancelToken.cancel("Operation canceled due to new request.");
        }

        cancelToken = axios.CancelToken.source();

        try {
            const results = await axios.get(`${baseUrl}/search/anime?q=${query}&page=1`,
                { cancelToken: cancelToken.token }
            );
            setResults(results.data.results);
            console.log(results.data);
        } catch (error) {
            console.log(error);
        }
        

    }

    return (
        <div>
            <div className="search-section">
                <div className="search-bar">
                    <h3>SEARCH</h3>
                    <input
                        type="text"
                        onChange={onChange}
                        value={search}
                        placeholder="Search..."
                    />
                </div>

                {
                    results.length > 0 ? (
                        <section className="result-card-list">
                            {results.map((shows) => (
                                <ResultCard anime={shows} />
                            ))}
                        </section>
                    ) : ""
                }
            </div>

            <div className="top">
                <h3>ALL TIME TOP</h3>
                {
                    <section className="result-card-list">
                        {topAnime.map((shows) => (
                            <ResultCard key={shows.mal_id} anime={shows} />
                        ))}
                    </section>

                }
            </div>
        </div>
    )



}
