import React, { useState } from 'react';
import '../Css/ListCard.css';
import '../Css/Popup.css';
import { Controls } from '../Global/Controls.js';

export const ListCard = ({ anime, type }) => {

    const [popup, togglePopup] = useState(false);

    return (
        <>
            {
                (popup === false) ? (
                    <div className="list-card">
                        <div className="image">
                            <img onClick={() => togglePopup(true)} alt={anime.title}
                                src={anime.image_url}
                            />
                        </div>
                        <div className="bottom-info">
                            <h5>{anime.title}</h5>
                            <Controls type={type} anime={anime} />
                        </div>
                    </div>
                ) : (
                        <div className="popup-container">
                            <div className="popup-content">
                                <div id="popup-cover" style={{ backgroundImage: `url(${anime.image_url})` }}>
                                </div>
                                <div className="popup-description">
                                    <div className="popup-header">
                                        <h1 id="popup-open-title">{anime.title}</h1>
                                        <button id="popup-open-toggle" onClick={() => togglePopup(false)}>X</button>
                                    </div>
                                    <div className="popup-info">
                                        <h4 id="popup-synopsis">{anime.synopsis}</h4>
                                    </div>
                                </div>


                            </div>

                        </div>
                    )
            }
        </>


    )
}
