import React, { useContext } from 'react';
import '../Css/ResultCard.css';
import { GlobalContext } from '../Global/GlobalState.js';

export const ResultCard = ({ anime }) => {

    const { addToWatchList, moveToWatched, activeAnimeList, watched } = useContext(GlobalContext);

    let storedAnime = activeAnimeList.find(o => o.mal_id === anime.mal_id);
    let storedAnimeWatched = watched.find(o => o.mal_id === anime.mal_id);

    //prevents you from adding already added anime
    const watchlistDisabled = storedAnime ? true :
        storedAnimeWatched ? true : false;

    const watchedDisabled = storedAnimeWatched ? true : false;

    return (
        <div key={anime.mal_id}>
            <div >
                <h4>{anime.title}</h4>

            </div>
            <div>
                <img alt={anime.title} src={anime.image_url}></img>
                <a href={anime.url}>Learn more</a>
            </div>
            <div>
                <button disabled={watchlistDisabled} onClick={() => addToWatchList(anime)}>Add to Watchlist</button>
                <button disabled={watchedDisabled} onClick={() => moveToWatched(anime)}>Add to Watched</button>

            </div>
        </div>
    )
}
