import React, { createContext, useReducer, useEffect } from 'react';
import AppReducer from './AppReducer.js';
//inital state

const initialState = {
    activeAnimeList: localStorage.getItem('watchlist') ?
        JSON.parse(localStorage.getItem('watchlist')) : [],
    watched: localStorage.getItem('watched') ?
        JSON.parse(localStorage.getItem('watched')) : [],
};

//create context

export const GlobalContext = createContext(initialState);

//provider components

export const GlobalProvider = props => {
    const [state, dispatch] = useReducer(AppReducer, initialState);

    useEffect(() => {
        localStorage.setItem('watchlist', JSON.stringify(state.activeAnimeList));
        localStorage.setItem('watched', JSON.stringify(state.watched));
    }, [state])

    //actions
    const addToWatchList = anime => {
        dispatch({ type: "ADD_TO_WATCH_LIST", payload: anime });
    };

    const removeFromWatchList = (id) => {
        dispatch({ type: "REMOVE_FROM_WATCH_LIST", payload: id });
    }

    const moveToWatched = anime => {
        dispatch({ type: "MOVE_TO_WATCHED", payload: anime});
    }

    const moveToWatching = anime => {
        dispatch({ type: "MOVE_TO_WATCHING", payload: anime});
    }

    return (
        <GlobalContext.Provider
            value={{
                activeAnimeList: state.activeAnimeList,
                watched: state.watched,
                addToWatchList,
                removeFromWatchList,
                moveToWatched,
                moveToWatching,
            }}>
            {props.children}
        </GlobalContext.Provider>
    )
}
